import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, TouchableOpacity, Vibration, View } from 'react-native';
import { Accelerometer } from 'expo-sensors';
import {Camera} from 'expo-camera';
import { Audio } from "expo-av";
let camera: Camera

const audioPlayer = new Audio.Sound();

  const HomeScreen = () => {
  const [cord, setData] = useState({x: 0,y: 0,z: 0,});
  const [subscription, setSubscription] = useState<any>(null);
  const [position, setPosition] = useState('horizontal');
  const [sound, setSound] = useState<any>();
  const [flashMode, setFlashMode] = React.useState('off')

  const [flagImage, setFlagImage] = useState(true);
  let imageAlarm = flagImage ? require('../utils/img/alarmaON.png') : require('../utils/img/alarmOFF.png');

  const _slow = () => {
    Accelerometer.setUpdateInterval(500);
  };

  const _fast = () => {
    Accelerometer.setUpdateInterval(16);
  };

  const _subscribe = () => {
    console.log('subscribe');
    setSubscription(
      Accelerometer.addListener(accelerometerData => {
        setData(accelerometerData);
      })
    );
  };

  const _unsubscribe = () => {
    subscription && subscription.remove();
    setSubscription(null);
  };


  useEffect(() => {
    _subscribe();
    return () => _unsubscribe();
  }, []);

  //iphone
  // useEffect(() => {
  //   const { x, y, z } = cord;
  //   if (x > 0.5 && x < 0.99) {
  //     setPosition('derecha');
  //   } else if (x < -0.5) {
  //     setPosition('izquierda');
  //   } else if (y < -0.3) {
  //     setPosition('vertical');
  //   } else if (z < -0.1) {
  //     setPosition('horizontal');
  //   }
  // }, [cord]);
  
  //Android cord
  useEffect(() => {
    const { x, y, z } = cord;

    if (x < 1 && x > 0.90 && y < 0.05 && y > 0.01) {
      setPosition('derecha');
    } else if (x < -0.5) {
      setPosition('izquierda');
    } else if (x > -0.01 && x < 0.5 && y > 1 && y < 1.05 && z > 0.01 && z < 0.19) {
      setPosition('vertical');
    } else if ( x< -0.00 && x > -0.05 && y > -0.05 && y < 0.05 && z > 0.05 && z < 2) {
      setPosition('horizontal');
    }
  }, [cord]);

  async function playSound(sound: any) {     
    try {
      await audioPlayer.unloadAsync()
      await audioPlayer.loadAsync(sound);
      await audioPlayer.playAsync();
    } catch (err) {
      console.warn("Couldn't Play audio", err)
    }
  }

  
   useEffect(() => {
    if(subscription){
      console.log("------------- " + position);
        switch(position){
         
        case 'horizontal':
         // Vibration.vibrate(1000);
          console.log('horizontal');
          (async ()=>{
            const {sound} = await Audio.Sound.createAsync(require('../utils/mp3/alarm2.mp3'));
            setSound(sound);
            await sound.playAsync()
          })()

          // playSound(require('../utils/mp3/alarm2.mp3'));
          // Vibration.vibrate(5000);   

          break;
        case 'izquierda':
          console.log('izquierda');
          (async ()=>{
            const {sound} = await Audio.Sound.createAsync(require('../utils/mp3/sacalamanodeahicarajo.mp3'));
            setSound(sound);
            await sound.playAsync()
          })()
          break;
        case 'derecha':
          console.log('derecha');
          (async ()=>{
            const {sound} = await Audio.Sound.createAsync(require('../utils/mp3/nogod.mp3'));
            setSound(sound);
            await sound.playAsync()
          })()
          break;
        case 'vertical':
          console.log('vertical');
          (async ()=>{
           const {sound} = await Audio.Sound.createAsync(require('../utils/mp3/alarma1.mp3'));
            setSound(sound);
            await sound.playAsync()
          })()
          break;
      }
    }
  }, [position])

  const { x, y, z } = cord;
  return (
    <View style={styles.container}>
      <Text style={styles.text}>Accelerometer: (in Gs where 1 G = 9.81 m s^-2)</Text>
      <Text style={styles.text}>
        x: {round(x)} y: {round(y)} z: {round(z)}
      </Text>
      <View style={styles.buttonContainer}>
        {/* <TouchableOpacity
          style={[styles.button, styles.middleButton]}
          onPress={__handleFlashMode}
        >
              <Camera
            flashMode={flashMode}

           ></Camera>
          <Text>Flash</Text>
        </TouchableOpacity> */}
        <TouchableOpacity onPress={subscription ? _unsubscribe : _subscribe} style={styles.button}>
          <Text>{subscription ? 'On' : 'Off'}</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={_slow} style={[styles.button, styles.middleButton]}>
          <Text>Slow</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={_fast} style={styles.button}>
          <Text>Fast</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

function round(n: number) {
  if (!n) {
    return 0;
  }
  return Math.floor(n * 100) / 100;
}

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 10,
  },
  text: {
    textAlign: 'center',
  },
  buttonContainer: {
    flexDirection: 'row',
    alignItems: 'stretch',
    marginTop: 15,
  },
  button: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#eee',
    padding: 10,
  },
  middleButton: {
    borderLeftWidth: 1,
    borderRightWidth: 1,
    borderColor: '#ccc',
  },
});
